@charset "UTF-8";

/********* public:notices.less ********/
.notices{list-style:none;margin:0;padding:0;background:hsla(var(--xf-contentBg));padding:6px;border-radius:10px;margin-bottom:15px}.notices.notices--block .notice{margin-bottom:7.5px}.notices.notices--floating{margin:0 20px 0 auto;width:300px;max-width:100%;z-index:800}@media (max-width:340px){.notices.notices--floating{margin-right:10px}}.notices.notices--floating .notice{margin-bottom:20px}.notices.notices--scrolling{display:flex;align-items:stretch;overflow:hidden;border:1px solid hsla(var(--xf-borderColor));margin-bottom:7.5px}.notices.notices--scrolling.notices--isMulti{margin-bottom:27.5px}.notices.notices--scrolling .notice{width:100%;flex-grow:0;flex-shrink:0;border:none}.notices .notice:last-child{margin-bottom:0}.notice{position:relative;border:1px solid hsla(var(--xf-borderColor))}.notice:before,.notice:after{content:" ";display:table}.notice:after{clear:both}.notice.notice--primary{color:hsla(var(--xf-textColor));background:hsla(var(--xf-contentHighlightBg))}.notice.notice--accent{color:hsla(var(--xf-textColor));background:hsla(var(--xf-contentAccentBg--h),var(--xf-contentAccentBg--s),var(--xf-contentAccentBg--l),10%);border-color:hsla(var(--xf-contentAccentBg--h),var(--xf-contentAccentBg--s),var(--xf-contentAccentBg--l),10%)}.notice.notice--accent a:not(.button--notice){color:hsla(var(--xf-textColor))}.notice.notice--dark{color:#fefefe;border:none;background:hsl(0,0%,8%)}.notice.notice--dark a:not(.button--notice){color:hsl(0,0%,71%)}.notice.notice--light{color:hsl(0,0%,8%);background:hsl(0,0%,100%)}.notice.notice--light a:not(.button--notice){color:hsl(0,0%,51%)}.notice.notice--enablePush{display:none}@media (max-width:900px){.notice.notice--enablePush{padding:3px 3px 10px;font-size:13px}}@media (max-width:900px){.notice.notice--cookie .notice-content{padding:3px 3px 10px;font-size:12px}.notice.notice--cookie .notice-content .button--notice{font-size:12px;padding:3px 6px}.notice.notice--cookie .notice-content .button--notice .button-text{font-size:12px}}.notice.notice--cookieAdvanced .notice-content{padding:1em 0}.notice.notice--cookieAdvanced .notice-content .u-pageCentered{display:grid;grid-template-columns:1fr 250px;gap:15px;align-items:end}@media (max-width:650px){.notice.notice--cookieAdvanced .notice-content .u-pageCentered{display:block}}.notice.notice--cookieAdvanced .notice-content h2{margin:0}.notice.notice--cookieAdvanced .notice-content .button{width:100%;margin:.5em 0;font-weight:bold}.notice.notice--cookieAdvanced .notice-content form.is-active{display:contents;grid-column:span 2}.notice.notice--cookieAdvanced .notice-content form.is-active .formRow.formRow--fullWidth>dd{padding-left:0;padding-right:0}.notice.notice--cookieAdvanced .notice-content form:not(.is-active){display:none}.notices--block .notice{font-size:15px;border-radius:10px}.notices--floating .notice{font-size:11px;border-radius:4px;box-shadow:1px 1px 3px hsla(0,0%,0%,.25)}.notices--floating .notice.notice--primary{background-color:hsla(var(--xf-contentHighlightBg--h),var(--xf-contentHighlightBg--s),var(--xf-contentHighlightBg--l),80%)}.notices--floating .notice.notice--accent{background-color:hsla(var(--xf-contentAccentBg--h),var(--xf-contentAccentBg--s),var(--xf-contentAccentBg--l),80%)}.notices--floating .notice.notice--dark{background-color:hsla(0,0%,8%,80%)}.notices--floating .notice.notice--light{background-color:hsla(0,0%,100%,80%)}.has-js .notices--floating .notice{display:none}.notice.notice--hasImage .notice-content{margin-left:68px;min-height:68px}@media (max-width:900px){.notice.notice--hidewide:not(.is-vis-processed){display:none;visibility:hidden}}@media (max-width:650px){.notice.notice--hidemedium:not(.is-vis-processed){display:none;visibility:hidden}}@media (max-width:480px){.notice.notice--hidenarrow:not(.is-vis-processed){display:none;visibility:hidden}}.notice-image{float:left;padding:10px 0 10px 10px}.notice-image img{max-width:48px;max-height:48px}.notice-content{padding:10px}.notice-content a.notice-dismiss{float:right;color:inherit;font-size:16px;line-height:1;height:1em;box-sizing:content-box;padding:0 0 5px 5px;opacity:.5;-webkit-transition: opacity .25s ease;transition: opacity .25s ease;cursor:pointer}.notice-content a.notice-dismiss:before{display:inline-block;content:"";height:1em;vertical-align:-0.125em;background-color:currentColor;width:.75em;mask:url('/styles/fa/regular/times.svg?v=5.15.3') no-repeat center;-webkit-mask:url('/styles/fa/regular/times.svg?v=5.15.3') no-repeat center}.notice-content a.notice-dismiss:hover{text-decoration:none;opacity:1}.notices--floating .notice-content a.notice-dismiss{font-size:14px}